  #{marketcap,jdbcType=BIGINT}, #{ipoyear,jdbcType=INTEGER}, #{sector,jdbcType=VARCHAR}, 
  #{industry,jdbcType=VARCHAR}, #{date,jdbcType=DATE}, #{currency,jdbcType=VARCHAR}
  )
